define(function() {
    return {
        applyAnimations: function() {
            $.fn.extend({
                animateCss: function(e) {
                    var t = "webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend";
                    this.addClass("animated " + e).one(t, function() {
                        $(this).removeClass("animated " + e)
                    })
                }
            });
            
            var buttons = [
                "a.button",
                "div.contact-form input[type='submit']",
                "a.icon.home",
                "a.icon.about",
                "a.icon.portfolio",
                "a.icon.contact",
                "a.icon.twitter",
                "a.icon.linkedin",
                "a.icon.pinterest",
                "a.icon.instagram",
                "a.icon.tumblr"
            ]
            
            for (var a=0; a<buttons.length; a++){
                var b = buttons[a];
                $('body').find(b).on("mouseenter", function() {
                var e = $(this);
                $(this).animateCss("rubberBand")
            });
            };
            
            
            var e = ["aboutme", "services", "portfolio", "contact"];
            require(["waypoints"], function(t) {
                $("ul.work-types").css("opacity", "0"), $("ul.work-items").css("opacity", "0");
                for (var i = 0; i < e.length; i++) {
                    var a = e[i];
                    $("body").find($("section." + a)).find(".sectionwrapper").css("opacity", 0).waypoint(function(e) {
                        if ("down" === e) {
                            var t = $(this.element).parent("section").attr("class");
                            switch (t) {
                                default: 0 == $(this.element).css("opacity") && $(this.element).animate({
                                    opacity: 1
                                }).animateCss("fadeIn");
                                break;
                                case "services":
                                        if (0 == $(this.element).css("opacity")) {
                                        $("ul.service-items li").css("opacity", 0), $(this.element).animate({
                                            opacity: 1
                                        }).animateCss("fadeIn");
                                        for (var i = 0; i < $("ul.service-items li").length; i++) {
                                            var n = 1e3 * i;
                                            setTimeout($("ul.service-items").find("li").eq(i).animate({
                                                opacity: 1
                                            }).animateCss("fadeInUp"), n)
                                        }
                                    }break;
                                case "portfolio":
                                        0 == $(this.element).css("opacity") && ($(this.element).animate({
                                        opacity: 1
                                    }).animateCss("fadeIn"), $("ul.work-types").animate({
                                        opacity: 1
                                    }).animateCss("fadeInLeft"), $(".work-items").animate({
                                        opacity: 1
                                    }).animateCss("fadeInRight"))
                            }
                        }
                    }, {
                        offset: 800
                    })
                }
            })
        }
    }
});
